// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;

import {SafeERC20, IERC20Permit} from "../../../shared/SafeERC20.sol";
import {MinterModifiers} from "../../../minter/MinterModifiers.sol";
import {DiamondModifiers, Role} from "../../../diamond/DiamondModifiers.sol";
import {ms} from "../../MinterStorage.sol";
import {cps} from "../CollateralPoolState.sol";
import {Constants} from "../../MinterTypes.sol";
import {Arrays} from "../../../libs/Arrays.sol";
import {WadRay} from "../../../libs/WadRay.sol";
import {ICollateralPoolConfigFacet, PoolCollateral, PoolKrAsset} from "../interfaces/ICollateralPoolConfigFacet.sol";

contract CollateralPoolConfigFacet is ICollateralPoolConfigFacet, DiamondModifiers, MinterModifiers {
    using SafeERC20 for IERC20Permit;
    using Arrays for address[];

    /// @inheritdoc ICollateralPoolConfigFacet
    function initialize(CollateralPoolConfig memory _config) external onlyOwner {
        require(_config.mcr >= Constants.MIN_COLLATERALIZATION_RATIO, "mcr-too-low");
        require(_config.lt >= Constants.MIN_COLLATERALIZATION_RATIO, "lt-too-low");
        require(_config.lt <= _config.mcr, "lt-too-high");
        require(_config.swapFeeRecipient != address(0), "invalid-fee-receiver");
        require(address(_config.positions) != address(0), "invalid-positions");

        cps().minimumCollateralizationRatio = _config.mcr;
        cps().liquidationThreshold = _config.lt;
        cps().swapFeeRecipient = _config.swapFeeRecipient;
        cps().positions = _config.positions;
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function getCollateralPoolConfig() external view override returns (CollateralPoolConfig memory) {
        return
            CollateralPoolConfig({
                swapFeeRecipient: cps().swapFeeRecipient,
                mcr: cps().minimumCollateralizationRatio,
                lt: cps().liquidationThreshold,
                positions: cps().positions
            });
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function setPoolMinimumCollateralizationRatio(uint256 _mcr) external onlyRole(Role.ADMIN) {
        require(_mcr >= Constants.MIN_COLLATERALIZATION_RATIO, "mcr-too-low");
        cps().minimumCollateralizationRatio = _mcr;
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function setPoolLiquidationThreshold(uint256 _lt) external onlyRole(Role.ADMIN) {
        require(_lt >= Constants.MIN_COLLATERALIZATION_RATIO, "mcr-too-low");
        require(_lt <= cps().minimumCollateralizationRatio, "lt-too-high");
        cps().liquidationThreshold = _lt;
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function enablePoolCollaterals(
        address[] calldata _enabledCollaterals,
        PoolCollateral[] memory _configurations
    ) external onlyRole(Role.ADMIN) {
        require(_enabledCollaterals.length == _configurations.length, "collateral-length-mismatch");
        for (uint256 i; i < _enabledCollaterals.length; i++) {
            // Checks
            require(ms().collateralAssets[_enabledCollaterals[i]].uintPrice() != 0, "collateral-no-price");
            require(
                _configurations[i].liquidationIncentive >= Constants.MIN_LIQUIDATION_INCENTIVE_MULTIPLIER,
                "li-too-low"
            );
            require(
                _configurations[i].liquidationIncentive <= Constants.MAX_LIQUIDATION_INCENTIVE_MULTIPLIER,
                "li-too-high"
            );
            require(cps().poolCollateral[_enabledCollaterals[i]].liquidityIndex == 0, "collateral-already-enabled");

            // We don't care what values are set for decimals or liquidityIndex. Overriding.
            _configurations[i].decimals = IERC20Permit(_enabledCollaterals[i]).decimals();
            _configurations[i].liquidityIndex = uint128(WadRay.RAY);

            // Save to state
            cps().poolCollateral[_enabledCollaterals[i]] = _configurations[i];
            cps().isEnabled[_enabledCollaterals[i]] = true;
            cps().collaterals.push(_enabledCollaterals[i]);
        }
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function enablePoolKrAssets(
        address[] calldata _enabledKrAssets,
        PoolKrAsset[] memory _configurations
    ) external onlyRole(Role.ADMIN) {
        require(_enabledKrAssets.length == _configurations.length, "krasset-length-mismatch");
        for (uint256 i; i < _enabledKrAssets.length; i++) {
            // Checks
            require(ms().kreskoAssets[_enabledKrAssets[i]].uintPrice() != 0, "krasset-no-price");
            require(cps().poolKrAsset[_enabledKrAssets[i]].supplyLimit == 0, "krasset-already-enabled");
            require(_configurations[i].supplyLimit > 0, "krasset-supply-limit-zero");
            require(
                _configurations[i].protocolFee <= Constants.MAX_COLLATERAL_POOL_PROTOCOL_FEE,
                "krasset-protocol-fee-too-high"
            );

            // Save to state
            cps().poolKrAsset[_enabledKrAssets[i]] = _configurations[i];
            cps().isEnabled[_enabledKrAssets[i]] = true;
            cps().krAssets.push(_enabledKrAssets[i]);
        }
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function updatePoolKrAsset(address _asset, PoolKrAsset calldata _configuration) external onlyRole(Role.ADMIN) {
        cps().poolKrAsset[_asset] = _configuration;
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function updatePoolCollateral(address _asset, uint256 _newLiquiditationIncentive) external onlyRole(Role.ADMIN) {
        require(_newLiquiditationIncentive >= Constants.MIN_LIQUIDATION_INCENTIVE_MULTIPLIER, "li-too-low");
        require(_newLiquiditationIncentive <= Constants.MAX_LIQUIDATION_INCENTIVE_MULTIPLIER, "li-too-high");
        cps().poolCollateral[_asset].liquidationIncentive = _newLiquiditationIncentive;
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function disablePoolCollaterals(address[] calldata _disabledAssets) external onlyRole(Role.ADMIN) {
        require(_disabledAssets.length > 0, "collateral-disable-length-0");
        address[] memory enabledCollaterals = cps().collaterals;
        bool didDisable;
        // Loopdy by disabled assets in.
        for (uint256 i; i < _disabledAssets.length; i++) {
            address disabledAsset = _disabledAssets[i];
            // Remove the assets from enabled list.
            for (uint256 j; j < enabledCollaterals.length; j++) {
                if (disabledAsset == enabledCollaterals[j]) {
                    cps().isEnabled[disabledAsset] = false;
                    didDisable = true;
                }
            }
        }
        require(didDisable, "collateral-disable-not-found");
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function disablePoolKrAssets(address[] calldata _disabledAssets) external onlyRole(Role.ADMIN) {
        require(_disabledAssets.length > 0, "krasset-disable-length-0");
        address[] memory enabledKrAssets = cps().krAssets;
        bool didDisable;
        // Loopdy by disabled assets in.
        for (uint256 i; i < _disabledAssets.length; i++) {
            address disabledAsset = _disabledAssets[i];
            // Remove the assets from enabled list.
            for (uint256 j; j < enabledKrAssets.length; j++) {
                if (disabledAsset == enabledKrAssets[j]) {
                    cps().isEnabled[disabledAsset] = false;
                    didDisable = true;
                }
            }
        }
        require(didDisable, "krasset-disable-not-found");
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function removePoolCollaterals(address[] calldata _removedAssets) external onlyRole(Role.ADMIN) {
        require(_removedAssets.length > 0, "collateral-remove-length-0");
        address[] memory enabledCollaterals = cps().collaterals;
        bool didRemove;
        // Loopdy by disabled assets in.
        for (uint256 i; i < _removedAssets.length; i++) {
            address removedAsset = _removedAssets[i];
            // Remove the assets from enabled list.
            for (uint256 j; j < enabledCollaterals.length; j++) {
                if (removedAsset == enabledCollaterals[j]) {
                    require(cps().totalDeposits[removedAsset] == 0, "remove-collateral-has-deposits");
                    cps().isEnabled[removedAsset] = false;
                    cps().collaterals.removeAddress(removedAsset, j);
                    didRemove = true;
                }
            }
        }
        require(didRemove, "collateral-remove-not-found");
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function removePoolKrAssets(address[] calldata _removedAssets) external onlyRole(Role.ADMIN) {
        require(_removedAssets.length > 0, "krasset-disable-length-0");
        address[] memory enabledKrAssets = cps().krAssets;
        bool didRemove;
        // Loopdy by disabled assets in.
        for (uint256 i; i < _removedAssets.length; i++) {
            address removedAsset = _removedAssets[i];
            cps().isEnabled[removedAsset] = false;
            // Remove the assets from enabled list.
            for (uint256 j; j < enabledKrAssets.length; j++) {
                if (removedAsset == enabledKrAssets[j]) {
                    // Make sure the asset has no debt.
                    require(cps().debt[removedAsset] == 0, "remove-krasset-has-debt");
                    cps().krAssets.removeAddress(removedAsset, j);
                    didRemove = true;
                }
            }
        }
        require(didRemove, "krasset-remove-not-found");
    }

    /* -------------------------------------------------------------------------- */
    /*                                    Swap                                    */
    /* -------------------------------------------------------------------------- */
    /// @inheritdoc ICollateralPoolConfigFacet
    function setFees(
        address _krAsset,
        uint256 _openFee,
        uint256 _closeFee,
        uint256 _protocolFee
    ) external onlyRole(Role.ADMIN) {
        cps().poolKrAsset[_krAsset].openFee = _openFee;
        cps().poolKrAsset[_krAsset].closeFee = _closeFee;
        cps().poolKrAsset[_krAsset].protocolFee = _protocolFee;
        emit FeeSet(_krAsset, _openFee, _closeFee, _protocolFee);
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function setSwapPairs(PairSetter[] calldata _pairs) external onlyRole(Role.ADMIN) {
        for (uint256 i; i < _pairs.length; i++) {
            cps().isSwapEnabled[_pairs[i].assetIn][_pairs[i].assetOut] = _pairs[i].enabled;
            cps().isSwapEnabled[_pairs[i].assetOut][_pairs[i].assetIn] = _pairs[i].enabled;
            emit PairSet(_pairs[i].assetIn, _pairs[i].assetOut, _pairs[i].enabled);
            emit PairSet(_pairs[i].assetOut, _pairs[i].assetIn, _pairs[i].enabled);
        }
    }

    /// @inheritdoc ICollateralPoolConfigFacet
    function setSwapPairsSingle(PairSetter calldata _pair) external onlyRole(Role.ADMIN) {
        cps().isSwapEnabled[_pair.assetIn][_pair.assetOut] = _pair.enabled;
        emit PairSet(_pair.assetIn, _pair.assetOut, _pair.enabled);
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;

import {SafeERC20, IERC20Permit} from "../../../shared/SafeERC20.sol";
import {DiamondModifiers} from "../../../diamond/DiamondModifiers.sol";
import {ms} from "../../MinterStorage.sol";
import {CollateralAsset, KrAsset} from "../../MinterTypes.sol";
import {ICollateralPoolFacet} from "../interfaces/ICollateralPoolFacet.sol";
import {cps} from "../CollateralPoolState.sol";
import {LibAmounts} from "../libs/LibAmounts.sol";
import {LibCalculation} from "../../libs/LibCalculation.sol";
import {LibDecimals} from "../../libs/LibDecimals.sol";
import {Error} from "../../../libs/Errors.sol";
import {WadRay} from "../../../libs/WadRay.sol";

contract CollateralPoolFacet is ICollateralPoolFacet, DiamondModifiers {
    using SafeERC20 for IERC20Permit;
    using WadRay for uint256;
    using LibDecimals for uint8;

    /// @inheritdoc ICollateralPoolFacet
    function poolDeposit(address _account, address _collateralAsset, uint256 _amount) external nonReentrant {
        // Transfer tokens into this contract prior to any state changes as an extra measure against re-entrancy.
        IERC20Permit(_collateralAsset).safeTransferFrom(msg.sender, address(this), _amount);

        // Record the collateral deposit.
        cps().recordCollateralDeposit(_account, _collateralAsset, _amount);

        emit CollateralPoolDeposit(_account, _collateralAsset, _amount);
    }

    /// @inheritdoc ICollateralPoolFacet
    function poolWithdraw(address _account, address _collateralAsset, uint256 _amount) external nonReentrant {
        // When principal deposits are less or equal to requested amount. We send full deposit + fees in this case.
        (uint256 collateralOut, uint256 feesOut) = cps().recordCollateralWithdrawal(
            msg.sender,
            _collateralAsset,
            _amount
        );

        // ensure that global pool is left with CR over MCR.
        require(cps().checkRatio(cps().minimumCollateralizationRatio), "withdraw-mcr-violation");

        // Send out the collateral.
        IERC20Permit(_collateralAsset).safeTransfer(_account, collateralOut + feesOut);

        // Emit event.
        emit CollateralPoolWithdraw(_account, _collateralAsset, collateralOut, feesOut);
    }

    /// @inheritdoc ICollateralPoolFacet
    function poolRepay(address _repayKrAsset, uint256 _repayAmount, address _seizeCollateral) external nonReentrant {
        require(cps().debt[_repayKrAsset] >= 0, "repay-no-debt");
        require(cps().debt[_repayKrAsset] >= _repayAmount, "repay-too-much");
        require(cps().swapDeposits[_seizeCollateral] >= 0, "repay-no-assets-available");

        uint256 seizedAmount = ms().kreskoAssets[_repayKrAsset].uintUSD(_repayAmount).wadDiv(
            ms().collateralAssets[_seizeCollateral].uintPrice()
        );
        require(cps().swapDeposits[_seizeCollateral] >= seizedAmount, "repay-too-much");

        cps().debt[_repayKrAsset] -= ms().repaySwap(_repayKrAsset, _repayAmount, msg.sender);

        uint256 amountOutInternal = LibAmounts.getCollateralAmountWrite(_repayKrAsset, seizedAmount);
        cps().swapDeposits[_seizeCollateral] -= amountOutInternal;
        cps().totalDeposits[_seizeCollateral] -= amountOutInternal;

        // solhint-disable-next-line avoid-tx-origin
        emit CollateralPoolRepayment(tx.origin, _repayKrAsset, _repayAmount, _seizeCollateral, seizedAmount);
    }

    function poolIsLiquidatable() external view returns (bool) {
        return cps().isLiquidatable();
    }

    /// @inheritdoc ICollateralPoolFacet
    function poolLiquidate(
        address _repayKrAsset,
        uint256 _repayAmount,
        address _seizeCollateral
    ) external nonReentrant {
        require(_repayAmount > 0, "liquidate-zero-amount");
        require(cps().debt[_repayKrAsset] >= _repayAmount, "liquidate-too-much");
        require(cps().isLiquidatable(), "not-liquidatable");

        KrAsset memory krAsset = ms().kreskoAssets[_repayKrAsset];
        CollateralAsset memory collateral = ms().collateralAssets[_seizeCollateral];
        uint256 repayAmountUSD = krAsset.uintUSD(_repayAmount);

        require(
            ms().getMaxLiquidation(address(0), krAsset, _seizeCollateral) >= repayAmountUSD,
            Error.LIQUIDATION_OVERFLOW
        );

        uint256 seizeAmount = collateral.decimals.fromWad(
            LibCalculation.calculateAmountToSeize(
                collateral.liquidationIncentive,
                collateral.uintPrice(),
                repayAmountUSD
            )
        );

        cps().debt[_repayKrAsset] -= ms().repaySwap(_repayKrAsset, _repayAmount, msg.sender);
        cps().adjustSeizedCollateral(_seizeCollateral, seizeAmount);

        IERC20Permit(_seizeCollateral).safeTransfer(msg.sender, seizeAmount);

        emit CollateralPoolLiquidationOccured(
            // solhint-disable-next-line avoid-tx-origin
            tx.origin,
            _repayKrAsset,
            _repayAmount,
            _seizeCollateral,
            seizeAmount
        );
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;
import {ICollateralPoolStateFacet} from "../interfaces/ICollateralPoolStateFacet.sol";
import {cps, PoolCollateral, PoolKrAsset} from "../CollateralPoolState.sol";
import {ms} from "../../MinterStorage.sol";
import {WadRay} from "../../../libs/WadRay.sol";

/**
 * @title CollateralPoolStateFacet
 * @author Kresko
 * @notice  This facet is used to view the state of the collateral pool.
 */
contract CollateralPoolStateFacet is ICollateralPoolStateFacet {
    using WadRay for uint256;

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountDepositsWithFees(
        address _account,
        address _collateralAsset
    ) external view returns (uint256) {
        return cps().getAccountDepositsWithFees(_account, _collateralAsset);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountPrincipalDeposits(
        address _account,
        address _collateralAsset
    ) external view returns (uint256) {
        return cps().getAccountPrincipalDeposits(_account, _collateralAsset);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountDepositsValue(
        address _account,
        address _collateralAsset,
        bool _ignoreFactors
    ) external view returns (uint256) {
        uint256 principalDeposits = cps().getAccountPrincipalDeposits(_account, _collateralAsset);
        uint256 scaledDeposits = cps().getAccountDepositsWithFees(_account, _collateralAsset);

        (uint256 assetValue, ) = ms().getCollateralValueAndOraclePrice(
            _collateralAsset,
            principalDeposits > scaledDeposits ? scaledDeposits : principalDeposits,
            _ignoreFactors
        );

        return assetValue;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountDepositsValueWithFees(
        address _account,
        address _collateralAsset
    ) external view returns (uint256) {
        (uint256 assetValue, ) = ms().getCollateralValueAndOraclePrice(
            _collateralAsset,
            cps().getAccountDepositsWithFees(_account, _collateralAsset),
            true
        );

        return assetValue;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountTotalDepositsValue(address _account, bool _ignoreFactors) external view returns (uint256) {
        return cps().getAccountTotalDepositValuePrincipal(_account, _ignoreFactors);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAccountTotalDepositsValueWithFees(address _account) external view returns (uint256) {
        return cps().getAccountTotalDepositValueWithFees(_account);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolDeposits(address _collateralAsset) external view returns (uint256) {
        return cps().getPoolDeposits(_collateralAsset);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolDepositsValue(address _collateralAsset, bool _ignoreFactors) external view returns (uint256) {
        (uint256 assetValue, ) = ms().getCollateralValueAndOraclePrice(
            _collateralAsset,
            cps().getPoolDeposits(_collateralAsset),
            _ignoreFactors
        );

        return assetValue;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolSwapDeposits(address _collateralAsset) external view returns (uint256) {
        return cps().getPoolSwapDeposits(_collateralAsset);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolDebt(address _kreskoAsset) external view returns (uint256) {
        return ms().getKreskoAssetAmount(_kreskoAsset, cps().debt[_kreskoAsset]);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolDebtValue(address _kreskoAsset, bool _ignoreFactors) external view returns (uint256) {
        return
            ms().getKrAssetValue(
                _kreskoAsset,
                ms().getKreskoAssetAmount(_kreskoAsset, cps().debt[_kreskoAsset]),
                _ignoreFactors
            );
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolCollateral(address _collateralAsset) external view returns (PoolCollateral memory) {
        return cps().poolCollateral[_collateralAsset];
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolCollateralAssets() external view returns (address[] memory) {
        return cps().collaterals;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolKrAsset(address _krAsset) external view returns (PoolKrAsset memory) {
        return cps().poolKrAsset[_krAsset];
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolKrAssets() external view returns (address[] memory) {
        return cps().krAssets;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolStats(
        bool _ignoreFactors
    ) external view returns (uint256 collateralValue, uint256 debtValue, uint256 cr) {
        collateralValue = cps().getTotalPoolDepositValue(_ignoreFactors);
        debtValue = cps().getTotalPoolKrAssetValueAtRatio(
            _ignoreFactors ? 1 ether : cps().minimumCollateralizationRatio,
            _ignoreFactors
        );
        if (debtValue == 0) return (collateralValue, debtValue, 0);
        cr = collateralValue.wadDiv(debtValue);
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolSwapFeeRecipient() external view returns (address) {
        return cps().swapFeeRecipient;
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolAssetIsEnabled(address _asset) external view returns (bool) {
        return cps().isEnabled[_asset];
    }

    /// @inheritdoc ICollateralPoolStateFacet
    function getPoolIsSwapEnabled(address _assetIn, address _assetOut) external view returns (bool) {
        return cps().isSwapEnabled[_assetIn][_assetOut];
    }
}

