// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;
import {PoolKrAsset, PoolCollateral} from "../CollateralPoolState.sol";
import {IPositionsFacet} from "../position/interfaces/IPositionsFacet.sol";

interface ICollateralPoolConfigFacet {
    /**
     * @notice Collateral pool configuration.
     * @param _swapFeeRecipient The swap fee recipient.
     * @param _mcr The minimum collateralization ratio.
     * @param _lt The liquidation threshold.
     */
    struct CollateralPoolConfig {
        address swapFeeRecipient;
        uint256 mcr;
        uint256 lt;
        IPositionsFacet positions;
    }
    // Emitted when a swap pair is disabled / enabled.
    event PairSet(address indexed assetIn, address indexed assetOut, bool enabled);
    // Emitted when a kresko asset fee is updated.
    event FeeSet(address indexed _asset, uint256 openFee, uint256 closeFee, uint256 protocolFee);

    // Emitted when a collateral is updated.
    event PoolCollateralUpdated(address indexed _asset, uint256 liquidationThreshold);

    // Emitted when a kresko asset is updated.
    event PoolKrAssetUpdated(
        address indexed _asset,
        uint256 openFee,
        uint256 closeFee,
        uint256 protocolFee,
        uint256 supplyLimit
    );

    // Used for setting swap pairs enabled or disabled in the pool.
    struct PairSetter {
        address assetIn;
        address assetOut;
        bool enabled;
    }

    /**
     * @notice Initialize the collateral pool.
     * Callable by diamond owner only.
     * @param _config The configuration for the pool.
     */
    function initialize(CollateralPoolConfig memory _config) external;

    /// @notice Get the pool configuration.
    function getCollateralPoolConfig() external view returns (CollateralPoolConfig memory);

    /// @notice Set the pool minimum collateralization ratio.
    function setPoolMinimumCollateralizationRatio(uint256 _mcr) external;

    /// @notice Set the pool liquidation threshold.
    function setPoolLiquidationThreshold(uint256 _lt) external;

    /**
     * @notice Enable kresko assets in the pool.
     * Only callable by admin.
     * @param _enabledKrAssets The list of KreskoAssets to enable
     * @param _configurations The configurations for the KreskoAssets. Must match length above.
     */
    function enablePoolKrAssets(address[] memory _enabledKrAssets, PoolKrAsset[] memory _configurations) external;

    /**
     * @notice Enable collaterals in the pool.
     * Only callable by admin.
     * @param _enabledCollaterals The list of collaterals to enable
     * @param _configurations The configurations for the collaterals. Must match length above.
     */
    function enablePoolCollaterals(
        address[] calldata _enabledCollaterals,
        PoolCollateral[] memory _configurations
    ) external;

    /**
     * @notice Update the KreskoAsset pool configuration.
     * Only callable by admin.
     * @param _asset The KreskoAsset to update
     * @param _configuration The configuration.
     * emits PoolKrAssetUpdated
     */
    function updatePoolKrAsset(address _asset, PoolKrAsset calldata _configuration) external;

    /**
     * @notice Update the collateral configuration.
     * Only callable by admin.
     * @param _asset The KreskoAsset to update
     * @param _newLiquidationIncentive The new liquidation incentive.
     * emits PoolCollateralUpdated
     */
    function updatePoolCollateral(address _asset, uint256 _newLiquidationIncentive) external;

    /**
     * @notice Disabled swaps and deposits for collaterals in the pool.
     * Only callable by admin.
     * @param _disabledAssets The list of collaterals to disable
     */
    function disablePoolCollaterals(address[] calldata _disabledAssets) external;

    /**
     * @notice Disabled swaps for krAssets in the pool.
     * Only callable by admin.
     * @param _disabledAssets The list of krAssets to disable
     */
    function disablePoolKrAssets(address[] calldata _disabledAssets) external;

    /**
     * @notice Completely removes collaterals from the pool.
     * Only callable by admin.
     * _removedAssets must not have any deposits.
     * @param _removedAssets The list of collaterals to remove
     */
    function removePoolCollaterals(address[] calldata _removedAssets) external;

    /**
     * @notice Completely remove KreskoAssets from the pool
     * Only callable by admin.
     * _removedAssets must not have any debt.
     * @param _removedAssets The list of KreskoAssets to remove
     */
    function removePoolKrAssets(address[] calldata _removedAssets) external;

    /**
     * @notice Set whether pairs are enabled or not. Both ways.
     * Only callable by admin.
     * @param _setters The configurations to set.
     */
    function setSwapPairs(PairSetter[] calldata _setters) external;

    /**
     * @notice Set whether a swap pair is enabled or not.
     * Only callable by admin.
     * @param _setter The configuration to set
     */
    function setSwapPairsSingle(PairSetter calldata _setter) external;

    /**
     * @notice Sets the fees for a kresko asset
     * @dev Only callable by admin.
     * @param _krAsset The kresko asset to set fees for.
     * @param _openFee The new open fee.
     * @param _closeFee The new close fee.
     * @param _protocolFee The protocol fee share.
     */
    function setFees(address _krAsset, uint256 _openFee, uint256 _closeFee, uint256 _protocolFee) external;
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;

interface ICollateralPoolFacet {
    event CollateralPoolDeposit(address indexed depositor, address indexed collateralAsset, uint256 amount);
    event CollateralPoolWithdraw(
        address indexed withdrawer,
        address indexed collateralAsset,
        uint256 amount,
        uint256 feeAmount
    );
    event CollateralPoolRepayment(
        address indexed repayer,
        address indexed repayKreskoAsset,
        uint256 repayAmount,
        address indexed receiveKreskoAsset,
        uint256 receiveAmount
    );

    event CollateralPoolLiquidationOccured(
        address indexed liquidator,
        address indexed repayKreskoAsset,
        uint256 repayAmount,
        address indexed seizeCollateral,
        uint256 seizeAmount
    );

    /**
     * @notice Deposit collateral for account to the collateral pool.
     * @param _account The account to deposit for.
     * @param _collateralAsset The collateral asset to deposit.
     * @param _amount The amount to deposit.
     */
    function poolDeposit(address _account, address _collateralAsset, uint256 _amount) external;

    /**
     * @notice Withdraw collateral for account from the collateral pool.
     * @param _account The account to withdraw for.
     * @param _collateralAsset The collateral asset to withdraw.
     * @param _amount The amount to withdraw.
     */
    function poolWithdraw(address _account, address _collateralAsset, uint256 _amount) external;

    /**
     * @notice Repay debt for no fees or slippage.
     * @notice Only uses swap deposits, if none available, reverts.
     * @param _repayKrAsset The asset to repay the debt in.
     * @param _repayAmount The amount of the asset to repay the debt with.
     * @param _seizeCollateral The collateral asset to seize.
     */
    function poolRepay(address _repayKrAsset, uint256 _repayAmount, address _seizeCollateral) external;

    /**
     * @notice Liquidate the collateral pool.
     * @notice Adjusts everyones deposits if swap deposits do not cover the seized amount.
     * @param _repayKrAsset The asset to repay the debt in.
     * @param _repayAmount The amount of the asset to repay the debt with.
     * @param _seizeCollateral The collateral asset to seize.
     */
    function poolLiquidate(address _repayKrAsset, uint256 _repayAmount, address _seizeCollateral) external;
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;
import {PoolCollateral, PoolKrAsset, CollateralPoolState} from "../CollateralPoolState.sol";

interface ICollateralPoolStateFacet {
    /**
     * @notice Get the collateral pool balance for `_account`.
     * @param _account The account.
     * @param _collateralAsset The collateral asset.
     */
    function getPoolAccountDepositsWithFees(address _account, address _collateralAsset) external view returns (uint256);

    /**
     * @notice Get the total collateral principal for `_account`
     * @param _account The account.
     * @param _collateralAsset The collateral asset
     */
    function getPoolAccountPrincipalDeposits(
        address _account,
        address _collateralAsset
    ) external view returns (uint256);

    /**
     * @notice Get the  collateral deposit value for `_account`
     * @param _account The account.
     * @param _collateralAsset The collateral asset
     * @param _ignoreFactors Ignore factors when calculating collateral value.
     */
    function getPoolAccountDepositsValue(
        address _account,
        address _collateralAsset,
        bool _ignoreFactors
    ) external view returns (uint256);

    /**
     * @notice Get the full value of account and fees for `_account`
     * @param _account The account.
     * @param _collateralAsset The collateral asset
     */
    function getPoolAccountDepositsValueWithFees(
        address _account,
        address _collateralAsset
    ) external view returns (uint256);

    /**
     * @notice Get the total collateral deposit value for `_account`
     * @param _account The account.
     * @param _ignoreFactors Ignore factors when calculating collateral value.
     */
    function getPoolAccountTotalDepositsValue(address _account, bool _ignoreFactors) external view returns (uint256);

    /**
     * @notice Get the full value of account and fees for `_account`
     * @param _account The account.
     */
    function getPoolAccountTotalDepositsValueWithFees(address _account) external view returns (uint256);

    /**
     * @notice Get the total collateral deposits for `_collateralAsset`
     * @param _collateralAsset The collateral asset
     */
    function getPoolDeposits(address _collateralAsset) external view returns (uint256);

    /**
     * @notice Get the total collateral deposit value for `_collateralAsset`
     * @param _collateralAsset The collateral asset
     * @param _ignoreFactors Ignore factors when calculating collateral and debt value.
     */
    function getPoolDepositsValue(address _collateralAsset, bool _ignoreFactors) external view returns (uint256);

    /**
     * @notice Get the total collateral swap deposits for `_collateralAsset`
     * @param _collateralAsset The collateral asset
     */
    function getPoolSwapDeposits(address _collateralAsset) external view returns (uint256);

    /**
     * @notice Get the collateral debt amount for `_kreskoAsset`
     * @param _kreskoAsset The KreskoAsset
     */
    function getPoolDebt(address _kreskoAsset) external returns (uint256);

    /**
     * @notice Get the collateral debt value for `_kreskoAsset`
     * @param _kreskoAsset The KreskoAsset
     * @param _ignoreFactors Ignore factors when calculating collateral and debt value.
     */
    function getPoolDebtValue(address _kreskoAsset, bool _ignoreFactors) external returns (uint256);

    /**
     * @notice Get the collateral configuration for `_collateralAsset`
     * @param _collateralAsset The collateral asset
     */
    function getPoolCollateral(address _collateralAsset) external returns (PoolCollateral memory);

    /**
     * @notice Get all pool CollateralAssets
     */
    function getPoolCollateralAssets() external returns (address[] memory);

    /**
     * @notice Get the collateral configuration for `_krAsset`
     * @param _krAsset The collateral asset
     */
    function getPoolKrAsset(address _krAsset) external returns (PoolKrAsset memory);

    /**
     * @notice Get all pool KreskoAssets
     */
    function getPoolKrAssets() external returns (address[] memory);

    /**
     * @notice Get pool collateral value, debt value and resulting CR.
     * @param _ignoreFactors Ignore factors when calculating collateral and debt value.
     */
    function getPoolStats(
        bool _ignoreFactors
    ) external returns (uint256 collateralValue, uint256 debtValue, uint256 cr);

    /**
     * @notice Get the swap fee recipient
     */
    function getPoolSwapFeeRecipient() external returns (address);

    /**
     * @notice Get enabled state of asset
     */
    function getPoolAssetIsEnabled(address _asset) external returns (bool);

    /**
     * @notice Get whether swap is enabled from `_assetIn` to `_assetOut`
     * @param _assetIn The asset to swap from
     * @param _assetOut The asset to swap to
     */
    function getPoolIsSwapEnabled(address _assetIn, address _assetOut) external view returns (bool);
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.19;
import {Position, NewPosition} from "../position/state/PositionsStorage.sol";

interface ICollateralPoolSwapFacet {
    event Swap(
        address indexed who,
        address indexed assetIn,
        address indexed assetOut,
        uint256 amountIn,
        uint256 amountOut
    );
    event SwapFee(address indexed assetIn, uint256 feeAmount, uint256 protocolFeeAmount);

    event Income(address asset, uint256 amount);

    /// @notice Get a price for an asset. It is `extOracleDecimals()` of precision.
    function getPrice(address _asset) external view returns (uint256 price);

    /**
     * @notice Preview the amount out received.
     * @param _assetIn The asset to pay with.
     * @param _assetOut The asset to receive.
     * @param _amountIn The amount of _assetIn to pay.
     * @param _leverage The leverage to use. 1e18 = 1x, 2e18 = 2x, etc.
     * @return amountOut The amount of `_assetOut` to receive according to `_amountIn`.
     */
    function previewSwap(
        address _assetIn,
        address _assetOut,
        uint256 _amountIn,
        uint256 _leverage
    ) external view returns (uint256 amountOut, uint256 feeAmount, uint256 protocolFee);

    /**
     * @notice Swap kresko assets with KISS using the shared collateral pool.
     * Uses oracle pricing of _amountIn to determine how much _assetOut to send.
     * @param _account The receiver of amount out.
     * @param _assetIn The asset to pay with.
     * @param _assetOut The asset to receive.
     * @param _amountIn The amount of _assetIn to pay.
     * @param _amountOutMin The minimum amount of _assetOut to receive, this is due to possible oracle price change.
     */
    function swap(
        address _account,
        address _assetIn,
        address _assetOut,
        uint256 _amountIn,
        uint256 _amountOutMin
    ) external;

    /// @notice deposit collateral into a position
    function positionDepositA(address _to, uint256 _amountA, Position memory _pos) external;

    /// @notice withdraw collateral from a position
    function positionWithdrawA(address _from, uint256 _amountA, Position memory _pos) external;

    /**
     * @notice Swap in to leverage. This is only callable by the positions NFT.
     * @param _sender The account that funds the position.
     * @param _pos The position to swap into.
     * @return amountAInAfterFee Amount in after fees are paid.
     * @return amountBOut Amount of `_assetOut` received.
     */
    function swapIntoLeverage(
        address _sender,
        NewPosition memory _pos
    ) external returns (uint256 amountAInAfterFee, uint256 amountBOut);

    /**
     * @notice Swaps out of leverage. This is only callable by the positions NFT.
     * @notice Called by the position contract.
     * @param _pos The position to swap out of.
     * @param _liquidator The incentive receiver if position is liquidated or closed by external caller.
     * @return amountAOut The amount of `_assetOut` to receive.
     */
    function swapOutOfLeverage(Position memory _pos, address _liquidator) external returns (uint256 amountAOut);

    /**
     * @notice Accumulates fees to deposits as a fixed, instantaneous income.
     * @param _incomeAsset the income asset
     * @param _amount amount to accumulate
     */
    function cumulateIncome(address _incomeAsset, uint256 _amount) external;
}

