pragma solidity <=0.8.19;

import {ERC721} from "../state/ERC721Storage.sol";
import {IONFT721CoreUpgradeable} from "../interfaces/IONFT721CoreUpgradeable.sol";
import {IONFT721Upgradeable} from "../interfaces/IONFT721Upgradeable.sol";
import {IERC721Upgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/IERC721Upgradeable.sol";
import {IERC721MetadataUpgradeable} from "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/IERC721MetadataUpgradeable.sol";
import {IERC165Upgradeable} from "@openzeppelin/contracts-upgradeable/utils/introspection/IERC165Upgradeable.sol";
import {ILayerZeroEndpointUpgradeable} from "../interfaces/ILayerZeroEndpointUpgradeable.sol";
import {ILayerZeroReceiverUpgradeable} from "../interfaces/ILayerZeroReceiverUpgradeable.sol";
import {ILayerZeroUserApplicationConfigUpgradeable} from "../interfaces/ILayerZeroUserApplicationConfigUpgradeable.sol";
import {lz, LibLZ, StoredCredit} from "../state/LZStorage.sol";
import {Meta} from "../../../../libs/Meta.sol";
import {ds} from "../../../../diamond/DiamondStorage.sol";
import {IERC165Facet} from "../../../../diamond/interfaces/IERC165Facet.sol";
import {BytesLib} from "../libs/BytesLib.sol";
import {ExcessivelySafeCall} from "../libs/ExcessivelySafeCall.sol";
import {DiamondModifiers} from "../../../../diamond/DiamondModifiers.sol";

contract LayerZeroFacet is
    IERC165Facet,
    IONFT721CoreUpgradeable,
    ILayerZeroReceiverUpgradeable,
    ILayerZeroUserApplicationConfigUpgradeable,
    DiamondModifiers
{
    using BytesLib for bytes;
    using ExcessivelySafeCall for address;

    function setupLayerZero(uint256 _minGasToTransfer, ILayerZeroEndpointUpgradeable _lzEndpoint) external onlyOwner {
        require(_minGasToTransfer != 0, LibLZ.MINGASZERO);
        lz().minGasToTransferAndStore = _minGasToTransfer;

        require(address(_lzEndpoint) != address(0), LibLZ.INVALID_ENDPOINT);
        lz().lzEndpoint = _lzEndpoint;
    }

    function getConfig(
        uint16 _version,
        uint16 _chainId,
        address,
        uint _configType
    ) external view returns (bytes memory) {
        return lz().lzEndpoint.getConfig(_version, _chainId, address(this), _configType);
    }

    // generic config for LayerZero user Application
    function setConfig(
        uint16 _version,
        uint16 _chainId,
        uint _configType,
        bytes calldata _config
    ) external override onlyOwner {
        lz().lzEndpoint.setConfig(_version, _chainId, _configType, _config);
    }

    /// @inheritdoc IERC165Facet
    function supportsInterface(
        bytes4 interfaceId
    ) external view override(IERC165Facet, IERC165Upgradeable) returns (bool) {
        return
            (interfaceId != 0xffffffff && interfaceId == type(IERC721Upgradeable).interfaceId) ||
            interfaceId == type(IERC721MetadataUpgradeable).interfaceId ||
            interfaceId == type(IONFT721Upgradeable).interfaceId ||
            interfaceId == type(IONFT721CoreUpgradeable).interfaceId ||
            ds().supportedInterfaces[interfaceId];
    }

    /// @inheritdoc IERC165Facet
    function setERC165(bytes4[] calldata interfaceIds, bytes4[] calldata interfaceIdsToRemove) external onlyOwner {
        for (uint256 i = 0; i < interfaceIds.length; i++) {
            ds().supportedInterfaces[interfaceIds[i]] = true;
        }

        for (uint256 i = 0; i < interfaceIdsToRemove.length; i++) {
            ds().supportedInterfaces[interfaceIdsToRemove[i]] = false;
        }
    }

    function forceResumeReceive(uint16 _srcChainId, bytes calldata _srcAddress) external override onlyOwner {
        lz().lzEndpoint.forceResumeReceive(_srcChainId, _srcAddress);
    }

    function setSendVersion(uint16 _version) external override onlyOwner {
        lz().lzEndpoint.setSendVersion(_version);
    }

    function setReceiveVersion(uint16 _version) external override onlyOwner {
        lz().lzEndpoint.setReceiveVersion(_version);
    }

    // _path = abi.encodePacked(remoteAddress, localAddress)
    // this function set the trusted path for the cross-chain communication
    function setTrustedRemote(uint16 _srcChainId, bytes calldata _path) external onlyOwner {
        lz().trustedRemoteLookup[_srcChainId] = _path;
        emit LibLZ.SetTrustedRemote(_srcChainId, _path);
    }

    function setTrustedRemoteAddress(uint16 _remoteChainId, bytes calldata _remoteAddress) external onlyOwner {
        lz().trustedRemoteLookup[_remoteChainId] = abi.encodePacked(_remoteAddress, address(this));
        emit LibLZ.SetTrustedRemoteAddress(_remoteChainId, _remoteAddress);
    }

    function getTrustedRemoteAddress(uint16 _remoteChainId) external view returns (bytes memory) {
        bytes memory path = lz().trustedRemoteLookup[_remoteChainId];
        require(path.length != 0, "LzApp: no trusted path record");
        return path.slice(0, path.length - 20); // the last 20 bytes should be address(this)
    }

    function setPrecrime(address _precrime) external onlyOwner {
        lz().precrime = _precrime;
        emit LibLZ.SetPrecrime(_precrime);
    }

    function setMinDstGas(uint16 _dstChainId, uint16 _packetType, uint _minGas) external onlyOwner {
        require(_minGas > 0, "LzApp: invalid minGas");
        lz().minDstGasLookup[_dstChainId][_packetType] = _minGas;
        emit LibLZ.SetMinDstGas(_dstChainId, _packetType, _minGas);
    }

    // if the size is 0, it means default size limit
    function setPayloadSizeLimit(uint16 _dstChainId, uint _size) external onlyOwner {
        lz().payloadSizeLimitLookup[_dstChainId] = _size;
    }

    //--------------------------- VIEW FUNCTION ----------------------------------------
    function isTrustedRemote(uint16 _srcChainId, bytes calldata _srcAddress) external view returns (bool) {
        bytes memory trustedSource = lz().trustedRemoteLookup[_srcChainId];
        return keccak256(trustedSource) == keccak256(_srcAddress);
    }

    function setMinGasToTransferAndStore(uint256 _minGasToTransferAndStore) external onlyOwner {
        require(_minGasToTransferAndStore > 0, LibLZ.MIN_GAS_NOT_ZERO);
        lz().minGasToTransferAndStore = _minGasToTransferAndStore;
    }

    // ensures enough gas in adapter params to handle batch transfer gas amounts on the dst
    function setDstChainIdToTransferGas(uint16 _dstChainId, uint256 _dstChainIdToTransferGas) external onlyOwner {
        require(_dstChainIdToTransferGas > 0, LibLZ.MIN_GAS_NOT_ZERO);
        lz().dstChainIdToTransferGas[_dstChainId] = _dstChainIdToTransferGas;
    }

    // limit on src the amount of tokens to batch send
    function setDstChainIdToBatchLimit(uint16 _dstChainId, uint256 _dstChainIdToBatchLimit) external onlyOwner {
        require(_dstChainIdToBatchLimit > 0, LibLZ.MIN_GAS_NOT_ZERO);
        lz().dstChainIdToBatchLimit[_dstChainId] = _dstChainIdToBatchLimit;
    }

    function sendFrom(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint _tokenId,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) external payable virtual override {
        require(_from == ds().contractOwner, "not supported yet");
        lz().send(
            _from,
            _dstChainId,
            _toAddress,
            _toSingletonArray(_tokenId),
            _refundAddress,
            _zroPaymentAddress,
            _adapterParams
        );
    }

    function sendBatchFrom(
        address _from,
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint[] memory _tokenIds,
        address payable _refundAddress,
        address _zroPaymentAddress,
        bytes memory _adapterParams
    ) external payable virtual override {
        require(_from == ds().contractOwner, "not supported yet");
        lz().send(_from, _dstChainId, _toAddress, _tokenIds, _refundAddress, _zroPaymentAddress, _adapterParams);
    }

    // Public function for anyone to clear and deliver the remaining batch sent tokenIds
    function clearCredits(bytes memory _payload) external {
        require(msg.sender == ds().contractOwner, "not supported yet");
        bytes32 hashedPayload = keccak256(_payload);
        require(lz().storedCredits[hashedPayload].creditsRemain, LibLZ.NO_CREDITS_STORED);

        (, uint[] memory tokenIds) = abi.decode(_payload, (bytes, uint[]));

        uint nextIndex = lz().creditTill(
            lz().storedCredits[hashedPayload].srcChainId,
            lz().storedCredits[hashedPayload].toAddress,
            lz().storedCredits[hashedPayload].index,
            tokenIds
        );
        require(nextIndex > lz().storedCredits[hashedPayload].index, LibLZ.NO_GAS_REMAINING);

        if (nextIndex == tokenIds.length) {
            // cleared the credits, delete the element
            delete lz().storedCredits[hashedPayload];
            emit CreditCleared(hashedPayload);
        } else {
            // store the next index to mint
            lz().storedCredits[hashedPayload] = StoredCredit(
                lz().storedCredits[hashedPayload].srcChainId,
                lz().storedCredits[hashedPayload].toAddress,
                nextIndex,
                true
            );
        }
    }

    function estimateSendFee(
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint _tokenId,
        bool _useZro,
        bytes memory _adapterParams
    ) public view virtual override returns (uint nativeFee, uint zroFee) {
        return lz().estimateFees(_dstChainId, _toAddress, _toSingletonArray(_tokenId), _useZro, _adapterParams);
    }

    function estimateSendBatchFee(
        uint16 _dstChainId,
        bytes memory _toAddress,
        uint[] memory _tokenIds,
        bool _useZro,
        bytes memory _adapterParams
    ) public view virtual override returns (uint nativeFee, uint zroFee) {
        return lz().estimateFees(_dstChainId, _toAddress, _tokenIds, _useZro, _adapterParams);
    }

    function _toSingletonArray(uint element) internal pure returns (uint[] memory) {
        uint[] memory array = new uint[](1);
        array[0] = element;
        return array;
    }

    function lzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) public virtual override {
        // lzReceive must be called by the endpoint for security
        require(Meta.msgSender() == address(lz().lzEndpoint), "LzApp: invalid endpoint caller");

        bytes memory trustedRemote = lz().trustedRemoteLookup[_srcChainId];
        // if will still block the message pathway from (srcChainId, srcAddress). should not receive message from untrusted remote.
        require(
            _srcAddress.length == trustedRemote.length &&
                trustedRemote.length > 0 &&
                keccak256(_srcAddress) == keccak256(trustedRemote),
            "LzApp: invalid source sending contract"
        );

        _blockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
    }

    // overriding the virtual function in LzReceiver
    function _blockingLzReceive(
        uint16 _srcChainId,
        bytes memory _srcAddress,
        uint64 _nonce,
        bytes memory _payload
    ) internal {
        (bool success, bytes memory reason) = address(this).excessivelySafeCall(
            gasleft(),
            150,
            abi.encodeWithSelector(this.nonblockingLzReceive.selector, _srcChainId, _srcAddress, _nonce, _payload)
        );
        // try-catch all errors/exceptions
        if (!success) {
            lz().failedMessages[_srcChainId][_srcAddress][_nonce] = keccak256(_payload);
            emit LibLZ.MessageFailed(_srcChainId, _srcAddress, _nonce, _payload, reason);
        }
    }

    function nonblockingLzReceive(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) external {
        // only internal transaction
        require(Meta.msgSender() == address(this), "NonblockingLzApp: caller must be LzApp");
        lz().nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
    }

    function retryMessage(
        uint16 _srcChainId,
        bytes calldata _srcAddress,
        uint64 _nonce,
        bytes calldata _payload
    ) external payable virtual {
        // assert there is message to retry
        bytes32 payloadHash = lz().failedMessages[_srcChainId][_srcAddress][_nonce];
        require(payloadHash != bytes32(0), "NonblockingLzApp: no stored message");
        require(keccak256(_payload) == payloadHash, "NonblockingLzApp: invalid payload");
        // clear the stored message
        lz().failedMessages[_srcChainId][_srcAddress][_nonce] = bytes32(0);
        // execute the message. revert if it fails again
        lz().nonblockingLzReceive(_srcChainId, _srcAddress, _nonce, _payload);
        emit LibLZ.RetryMessageSuccess(_srcChainId, _srcAddress, _nonce, payloadHash);
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity <=0.8.19;

import {ds} from "../../../../diamond/DiamondStorage.sol";
import {DiamondModifiers} from "../../../../diamond/DiamondModifiers.sol";
import {Error} from "../../../../libs/Errors.sol";
import {lz} from "../state/LZStorage.sol";
import {ERC721} from "../state/ERC721Storage.sol";
import {pos, LibPositions, PositionsInitializer} from "../state/PositionsStorage.sol";
import {ICollateralPoolSwapFacet} from "../../interfaces/ICollateralPoolSwapFacet.sol";

contract PositionsConfigFacet is DiamondModifiers {
    function initialize(PositionsInitializer memory _init) external {
        ds().contractOwner = msg.sender;
        require(ds().storageVersion == 1, Error.ALREADY_INITIALIZED);
        // check erc721
        require(bytes(_init.name).length > 0 && bytes(_init.symbol).length > 0, LibPositions.INVALID_NAME);
        ERC721().name = _init.name;
        ERC721().symbol = _init.symbol;
        // check liq threshold
        require(_init.liquidationThreshold > -1e18, LibPositions.INVALID_LT);
        require(_init.liquidationThreshold < 0.001e18, LibPositions.INVALID_LT);
        pos().liquidationThreshold = _init.liquidationThreshold;
        // check close threshold
        require(_init.closeThreshold <= 100e18, LibPositions.INVALID_LT); // 10,000% profit
        require(_init.closeThreshold >= 0.001e18, LibPositions.INVALID_LT); // 1% profit
        pos().closeThreshold = _init.closeThreshold;
        // check min/max lev
        require(_init.maxLeverage <= 500e18, LibPositions.INVALID_MAX_LEVERAGE);
        require(_init.maxLeverage >= 1e18, LibPositions.INVALID_MAX_LEVERAGE);
        require(_init.minLeverage >= 0.01e18, LibPositions.INVALID_MAX_LEVERAGE);
        require(_init.minLeverage < _init.maxLeverage, LibPositions.INVALID_MAX_LEVERAGE);
        pos().minLeverage = _init.minLeverage;
        pos().maxLeverage = _init.maxLeverage;
        // check kresko
        require(address(_init.kresko) != address(0), LibPositions.INVALID_KRESKO);
        pos().kresko = _init.kresko;
        ds().storageVersion = 1;
    }

    function getPositionsConfig() external view returns (PositionsInitializer memory) {
        return
            PositionsInitializer({
                kresko: pos().kresko,
                name: ERC721().name,
                symbol: ERC721().symbol,
                liquidationThreshold: pos().liquidationThreshold,
                closeThreshold: pos().closeThreshold,
                maxLeverage: pos().maxLeverage,
                minLeverage: pos().minLeverage
            });
    }

    function setLiquidationThreshold(int128 _threshold) external onlyOwner {
        require(_threshold <= 1e18, LibPositions.INVALID_LT);
        require(_threshold >= 0.1e18, LibPositions.INVALID_LT);

        pos().liquidationThreshold = _threshold;
    }

    function setCloseThreshold(int128 _threshold) external onlyOwner {
        require(_threshold <= 1e18, LibPositions.INVALID_LT);
        require(_threshold >= 0.01e18, LibPositions.INVALID_LT);

        pos().closeThreshold = _threshold;
    }

    function setMaxLeverage(uint256 _maxLeverage) external onlyOwner {
        require(_maxLeverage <= 500e18, LibPositions.INVALID_MAX_LEVERAGE);
        require(_maxLeverage >= 1e18, LibPositions.INVALID_MAX_LEVERAGE);

        pos().maxLeverage = _maxLeverage;
    }

    function setMinLeverage(uint256 _minLeverage) external onlyOwner {
        require(_minLeverage >= 0.01e18, LibPositions.INVALID_MAX_LEVERAGE);

        pos().minLeverage = _minLeverage;
    }

    function setKresko(ICollateralPoolSwapFacet _kresko) external onlyOwner {
        require(address(_kresko) != address(0), LibPositions.INVALID_KRESKO);

        pos().kresko = _kresko;
    }
}

// SPDX-License-Identifier: BUSL-1.1
pragma solidity <=0.8.19;
import {ds} from "../../../../diamond/DiamondStorage.sol";
import {DiamondModifiers} from "../../../../diamond/DiamondModifiers.sol";
import {WadRay} from "../../../../libs/WadRay.sol";
import {ERC721} from "../state/ERC721Storage.sol";
import {pos, LibPositions, NewPosition, Position} from "../state/PositionsStorage.sol";
import {IPositionsFacet} from "../interfaces/IPositionsFacet.sol";
import {IERC20Permit} from "../../../../shared/IERC20Permit.sol";

contract PositionsFacet is IPositionsFacet, DiamondModifiers {
    using WadRay for uint256;
    modifier check(uint256 _id) {
        address owner = ERC721().ownerOf(_id);
        require(
            msg.sender == owner || ERC721().isApprovedForAll(owner, msg.sender),
            LibPositions.ERROR_POSITION_NOT_OWNED_BY_CALLER
        );
        _;
    }

    /// @inheritdoc IPositionsFacet
    function createPosition(NewPosition memory _position) external returns (uint256 positionId) {
        (uint256 amountAFeeReduced, uint256 amountBOut) = pos().kresko.swapIntoLeverage(msg.sender, _position);
        positionId = ERC721().currentId++;

        pos().positions[positionId] = Position({
            account: _position.account,
            assetA: _position.assetA,
            assetB: _position.assetB,
            amountA: amountAFeeReduced,
            amountB: amountBOut,
            leverage: _position.leverage,
            valueBCache: pos().kresko.getPrice(_position.assetB).wadMul(amountBOut),
            liquidationIncentive: 0.05 ether,
            closeIncentive: 0.01 ether,
            creationTimestamp: block.timestamp,
            lastUpdateTimestamp: block.timestamp,
            nonce: 0
        });

        ERC721().safeMint(_position.account, positionId, "");
    }

    /// @inheritdoc IPositionsFacet
    function closePosition(uint256 _id) external {
        // check ownership
        address owner = ERC721().ownerOf(_id);
        if (msg.sender != owner) {
            // allow closing and liquidations from external accounts

            if (pos().isLiquidatable(_id) || pos().isCloseable(_id)) {
                pos().kresko.swapOutOfLeverage(pos().positions[_id], msg.sender);
                ERC721().burn(_id);
                return;
            }
            require(
                msg.sender == owner || ERC721().isApprovedForAll(owner, msg.sender),
                LibPositions.ERROR_POSITION_NOT_OWNED_BY_CALLER
            );
        }

        pos().kresko.swapOutOfLeverage(pos().positions[_id], address(0));
        ERC721().burn(_id);
    }

    /// @inheritdoc IPositionsFacet
    function buy(uint256 _id, uint256 _amountA, uint256 _amountBMin) external override check(_id) {
        (uint256 amountAAfterFee, uint256 amountBOut) = pos().kresko.swapIntoLeverage(
            msg.sender,
            NewPosition({
                account: pos().positions[_id].account,
                assetA: pos().positions[_id].assetA,
                assetB: pos().positions[_id].assetB,
                leverage: pos().positions[_id].leverage,
                amountA: _amountA,
                amountBMin: _amountBMin
            })
        );

        pos().positions[_id].amountA += amountAAfterFee;
        pos().positions[_id].amountB += amountBOut;
        pos().positions[_id].valueBCache += pos().kresko.getPrice(pos().positions[_id].assetB).wadMul(amountBOut);
        pos().positions[_id].lastUpdateTimestamp = block.timestamp;
    }

    /// @inheritdoc IPositionsFacet
    function deposit(uint256 _id, uint256 _amountA) external override {
        require(ERC721().exists(_id), "!exists");
        uint256 change = _amountA.wadDiv(pos().positions[_id].amountA);
        pos().kresko.positionDepositA(msg.sender, _amountA, pos().positions[_id]);

        pos().positions[_id].amountA += _amountA;
        pos().positions[_id].lastUpdateTimestamp = block.timestamp;
        pos().positions[_id].leverage = pos().getLeverage(_id);

        require(pos().positions[_id].leverage - change >= 1 ether, "!leverage-too-low");
    }

    /// @inheritdoc IPositionsFacet
    function withdraw(uint256 _id, uint256 _amountA) external override check(_id) {
        pos().kresko.positionWithdrawA(pos().positions[_id].account, _amountA, pos().positions[_id]);

        int256 change = int256(_amountA.wadDiv(pos().positions[_id].amountA));
        require(pos().getRatio(_id) - change >= pos().liquidationThreshold, "!leverage-too-high");

        pos().positions[_id].amountA -= _amountA;
        pos().positions[_id].lastUpdateTimestamp = block.timestamp;
        pos().positions[_id].leverage = pos().getLeverage(_id);
    }

    /// @inheritdoc IPositionsFacet
    function buyback(uint256 _id, uint256 _amountB) external override check(_id) {
        if (_amountB >= pos().positions[_id].amountB) {
            _amountB = pos().positions[_id].amountB;
            ERC721().burn(_id);
        }

        uint256 change = _amountB.wadDiv(pos().positions[_id].amountB);
        Position memory temp = pos().positions[_id];
        temp.amountB = temp.amountB.wadMul(change);
        temp.amountA = temp.amountA.wadMul(change);

        pos().kresko.swapOutOfLeverage(temp, address(0));

        pos().positions[_id].amountA -= temp.amountA;
        pos().positions[_id].amountB -= temp.amountB;
        pos().positions[_id].valueBCache -= pos().kresko.getPrice(pos().positions[_id].assetB).wadMul(_amountB);
        pos().positions[_id].lastUpdateTimestamp = block.timestamp;
    }

    /// @inheritdoc IPositionsFacet
    function getPosition(uint256 _id) external view returns (Position memory, int128 currentLeverage) {
        return (pos().getPosition(_id), int128(pos().getRatio(_id)));
    }

    /// @inheritdoc IPositionsFacet
    function isLiquidatable(uint256[] calldata _ids) external view override returns (bool[] memory results) {
        results = new bool[](_ids.length);
        for (uint256 i; i < _ids.length; i++) {
            results[i] = pos().isLiquidatable(_ids[i]);
        }
    }

    function getRatioOf(uint256 _id) external view returns (int128) {
        return int128(pos().getRatio(_id));
    }

    /// @inheritdoc IPositionsFacet
    function isClosable(uint256[] calldata _ids) external view override returns (bool[] memory results) {
        results = new bool[](_ids.length);
        for (uint256 i; i < _ids.length; i++) {
            results[i] = pos().isCloseable(_ids[i]);
        }
    }
}

